import React, { useState } from "react";
import { CSSTransition } from "react-transition-group";
import '../shadow_css/shadow.css';

const SmallWindow = ({ props, isOpenSmallWindow, closeSmallWindow }) => {
    const [currentMusic, setCurrentMusic] = useState(props[0]); // Изначально первый трек

    // Функция для переключения на другой трек
    const changeMusic = (music) => {
        setCurrentMusic(music);
    };

    return (
        <>
            <CSSTransition
                in={isOpenSmallWindow}
                timeout={500}
                classNames="small-modal"
                unmountOnExit
            >
                <div className="fixed bottom-0 left-0 right-0 bg-[#ffffff] h-[75px] mb-[75px] flex flex-nowrap border-0 custom-small-window-shadow">
                    <div className="h-[75px] w-[75px]">
                        <img className='' src={currentMusic.img} alt="Обложка трека" />
                    </div>
                    <div className="ml-[26px] flex flex-col mt-3">
                        <div className="pb-0 text-[22px] font-slim text-gray-800">
                            {currentMusic.title}
                        </div>
                        <div className="text-[12px] text-gray-700 font-light">
                            {currentMusic.authorName}
                        </div>
                    </div>
                    <div className="ml-auto mr-6 mt-4">
                        <button onClick={closeSmallWindow} className="mr-6">
                            Закрыть
                        </button>
                    </div>
                </div>
            </CSSTransition>

            {/* Пример списка треков для переключения */}
            <div className="track-list mt-5">
                {props.map((music) => (
                    <button key={music.id} onClick={() => changeMusic(music)}>
                        {music.title} - {music.authorName}
                    </button>
                ))}
            </div>
        </>
    );
};

export default SmallWindow;
