import React, { useMemo, useState } from 'react'
import Icon from '../templates/icons/Асхаб.png'
import SearchModal from './modals/SearchModal';

const Search = ({ musics }) => {
    const [searchQuery, setSearchQuery] = useState('')
    const [isOpenSearchBar, setCheckOpenSearchBar] = useState(false);

    const doSearh = useMemo(() => {
        if (!searchQuery) {
            return musics
        }

        return musics.filter((musics) =>
            musics.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
            musics.authorName.toLowerCase().includes(searchQuery.toLowerCase())
        )
    }, [musics, searchQuery])


    const openSearch = () => {
        setCheckOpenSearchBar(true);
    };
    const closeSearch = () => {
        setCheckOpenSearchBar(false);
    };

    return (
        <div className='flex pt-2 '>
            <div className={isOpenSearchBar === false
                ? 'relative h-[69px] w-full rounded-3xl bg-[#393442] shadow-2xl transition-all duration-300'
                : 'relative h-[120px] w-full rounded-3xl bg-[#393442] shadow-2xl transition-all duration-300'}>

                <input
                    onChange={(e) => setSearchQuery(e.target.value)}
                    onClick={openSearch}
                    value={searchQuery}
                    placeholder='Izdeu..'
                    type="text"
                    className='bg-[#FFFFFFAB;] rounded-full pl-5 ml-[17px] h-[38px] mt-[15px] w-[300px] focus:outline-none placeholder:text-gray-600 placeholder:font-semibold
                    hover:translate-x-12 hover:w-[330px] transition-all duration-500 '
                />
                <div className={isOpenSearchBar ? 'w-0 h-0' : 'float-right pb-10 mr-[16px] mt-[15px]'}>
                    <img className='w-10 h-10 rounded-full' src={Icon} alt="тут картинка" />
                </div>
                <div className='mb-2'>
                    <button onClick={closeSearch}>
                        <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke-width="1.5" stroke={!isOpenSearchBar ? "#393442" : "currentColor"} class="size-6">
                            <path stroke-linecap="round" stroke-linejoin="round" d="M10.5 19.5 3 12m0 0 7.5-7.5M3 12h18" />
                        </svg>
                    </button>
                </div>
            </div>

        </div >
    )



}

export default Search
