import React, { useState, useMemo } from 'react';

const items = [
    { id: 1, name: 'Apple' },
    { id: 2, name: 'Banana' },
    { id: 3, name: 'Orange' },
    { id: 4, name: 'Grapes' },
    { id: 5, name: 'Pineapple' },
];

const recentSearches = ['Apple', 'Banana'];

const SearchBar = () => {
    const [searchTerm, setSearchTerm] = useState('');
    const [showResults, setShowResults] = useState(false);

    const filteredItems = useMemo(() => {
        return items.filter(item =>
            item.name.toLowerCase().includes(searchTerm.toLowerCase())
        );
    }, [searchTerm]);

    const handleChange = (event) => {
        setSearchTerm(event.target.value);
        setShowResults(event.target.value.length > 0);
    };

    const handleFocus = () => {
        setShowResults(true);
    };

    const handleBlur = () => {
        setTimeout(() => setShowResults(false), 200); // Задержка для скрытия меню при потере фокуса
    };

    return (
        <div className="relative w-full max-w-md mx-auto">
            <input
                type="text"
                placeholder="Search..."
                value={searchTerm}
                onChange={handleChange}
                onFocus={handleFocus}
                onBlur={handleBlur}
                className="border p-2 w-full rounded-md"
            />
            {showResults && (
                <div className="absolute z-10 w-full bg-white border border-gray-300 mt-1 rounded-md shadow-lg">
                    {searchTerm.length === 0 ? (
                        <div className="p-2">
                            <h4 className="font-semibold text-gray-700 mb-2">Recent Searches</h4>
                            {recentSearches.map((search, index) => (
                                <div key={index} className="p-2 hover:bg-gray-100 cursor-pointer">
                                    {search}
                                </div>
                            ))}
                        </div>
                    ) : (
                        <div className="p-2">
                            {filteredItems.length > 0 ? (
                                filteredItems.map(item => (
                                    <div
                                        key={item.id}
                                        className="p-2 hover:bg-gray-100 cursor-pointer"
                                    >
                                        {item.name}
                                    </div>
                                ))
                            ) : (
                                <div className="p-2 text-gray-500">No results found</div>
                            )}
                        </div>
                    )}
                </div>
            )}
        </div>
    );
};

export default SearchBar;
