import React, { useMemo, useState } from 'react'
import Icon from '../templates/icons/Асхаб.png'

const Search = ({ musics }) => {
    const [searchQuery, setSearchQuery] = useState('')
    const [isOpenSearchBar, setCheckOpenSearchBar] = useState(false);

    const filteredMusics = useMemo(() => {
        if (!searchQuery) return musics;

        return musics.filter((music) =>
            music.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
            music.authorName.toLowerCase().includes(searchQuery.toLowerCase())
        );
    }, [musics, searchQuery]);

    const openSearch = () => setCheckOpenSearchBar(true);
    const closeSearch = () => setCheckOpenSearchBar(false);

    return (
        <div className="flex pt-2">
            <div className={`relative w-full rounded-3xl bg-[#393442] shadow-2xl transition-all duration-300 ${isOpenSearchBar ? 'h-[300px] absolute' : 'h-[69px] relative'}`}>
                <input
                    onChange={(e) => setSearchQuery(e.target.value)}
                    onClick={openSearch}
                    value={searchQuery}
                    placeholder="Izdeu.."
                    type="text"
                    className="bg-[#FFFFFFAB] rounded-full pl-5 ml-[17px] h-[38px] mt-[15px] w-[300px] focus:outline-none placeholder:text-gray-600 placeholder:font-semibold hover:w-[330px] transition-all duration-500"
                />
                {!isOpenSearchBar && (
                    <div className="float-right pb-10 mr-[16px] mt-[15px]">
                        <img className="w-10 h-10 rounded-full" src={Icon} alt="тут картинка" />
                    </div>
                )}
            </div>
            {isOpenSearchBar && (
                <div className="ml-5 absolute mt-[20px]">
                    <button onClick={closeSearch}>
                        <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={1.5} stroke="white" className="size-7">
                            <path strokeLinecap="round" strokeLinejoin="round" d="M10.5 19.5 3 12m0 0 7.5-7.5M3 12h18" />
                        </svg>
                    </button>
                </div>
            )}
        </div>
    )
}

export default Search;
