import React, { useState } from 'react';

const ModalExample = () => {
    const [isOpen, setIsOpen] = useState(false);

    // Открыть модальное окно
    const openModal = () => {
        setIsOpen(true);
    };

    // Закрыть модальное окно
    const closeModal = () => {
        setIsOpen(false);
    };

    return (
        <div className="h-screen flex items-center justify-center">
            <button
                className="px-4 py-2 bg-blue-500 text-white rounded"
                onClick={openModal}
            >
                Открыть модальное окно
            </button>

            {/* Модальное окно */}
            {isOpen && (
                <div className="fixed inset-0 bg-black bg-opacity-10 flex items-center justify-center">
                    <div className="bg-white p-6 rounded-lg shadow-lg relative w-[300px]">
                        <h2 className="text-xl font-semibold mb-4">Модальное окно</h2>
                        <p className="mb-4">Это пример модального окна на React с Tailwind CSS.</p>
                        <button
                            className="px-4 py-2 bg-red-500 text-white rounded"
                            onClick={closeModal}
                        >
                            Закрыть
                        </button>
                    </div>

                    {/* Клик вне окна закрывает модальное окно */}
                    <div
                        className="fixed inset-0"
                        onClick={closeModal}
                    ></div>
                </div>
            )}
        </div>
    );
};

export default ModalExample;
