import React, { useMemo, useState } from 'react';
import Icon from '../templates/icons/Асхаб.png';

const Search = ({ musics }) => {
    const [searchQuery, setSearchQuery] = useState('');
    const [isFocused, setIsFocused] = useState(false);

    const doSearch = useMemo(() => {
        if (!searchQuery) {
            return musics;
        }
        return musics.filter((music) =>
            music.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
            music.authorName.toLowerCase().includes(searchQuery.toLowerCase())
        );
    }, [musics, searchQuery]);

    const handleFocus = () => {
        setIsFocused(true);
    };

    const handleBlur = () => {
        // Удаляем фокус только если поле ввода пустое
        if (!searchQuery) {
            setIsFocused(false);
        }
    };

    return (
        <div className={`flex pt-2 transition-all duration-300 ${isFocused ? 'h-64' : 'h-16'}`}>
            <div className={`relative w-full rounded-3xl bg-[#393442] shadow-2xl transition-all duration-300 ${isFocused ? 'mb-4' : ''}`}>
                <input
                    onFocus={handleFocus}
                    onBlur={handleBlur}
                    onChange={(e) => setSearchQuery(e.target.value)}
                    value={searchQuery}
                    placeholder='Izdeu..'
                    type="text"
                    className='bg-[#FFFFFFAB] rounded-full pl-5 ml-[17px] h-[38px] w-[300px] placeholder:text-gray-600 placeholder:font-semibold'
                />
                <div className='float-right pb-10 mr-[16px] mt-3'>
                    <img className='w-10 h-10 rounded-full' src={Icon} alt="тут картинка" />
                </div>
            </div>
            {isFocused && (
                <div className="absolute top-full mt-2 w-full bg-white shadow-lg rounded-md">
                    <ul>
                        {doSearch.map((music) => (
                            <li key={music.id} className="p-2 hover:bg-gray-200">{music.title} - {music.authorName}</li>
                        ))}
                    </ul>
                </div>
            )}
        </div>
    );
};

export default Search;
