import React, { useMemo, useState } from 'react'
import Icon from '../templates/icons/Асхаб.png'

const Search = ({ musics }) => {
    const [searchQuery, setSearchQuery] = useState('')
    const [isOpenSearhBar, setCheckOpenSearchBar] = useState(false);

    const doSearh = useMemo(() => {
        if (!searchQuery) {
            return musics
        }

        return musics.filter((musics) =>
            musics.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
            musics.authorName.toLowerCase().includes(searchQuery.toLowerCase())
        )
    }, [musics, searchQuery])


    const openSearch = () => {
        setCheckOpenSearchBar(true);
    };
    const closeSearch = () => {
        setCheckOpenSearchBar(false);
    };

    return (
        <div className='flex pt-2 '>
            <div className='relative h-[69px] w-full rounded-3xl bg-[#393442;] shadow-2xl'>
                <input
                    onChange={(e) => setSearchQuery(e.target.value)}
                    onClick={openSearch}
                    value={searchQuery}
                    placeholder='Izdeu..'
                    type="text"
                    className='bg-[#FFFFFFAB;] rounded-full pl-5 ml-[17px] h-[38px] mt-3 w-[300px] focus:outline-none placeholder:text-gray-600 placeholder:font-semibold
                    hover:translate-x-12 duration-500'
                />
                <div className='float-right pb-10 mr-[16px] mt-3'>
                    <img className='w-10 h-10 rounded-full' src={Icon} alt="тут картинка" />
                </div>
            </div>
            <div>
                {isOpenSearhBar === true ? (<div className='float-right pb-10 mr-[16px] mt-3'>
                    <img className='w-10 h-10 rounded-full' src={Icon} alt="тут картинка" />
                </div>) : (
                    <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={1.5} stroke='currenColor' className="size-8 absolute">
                        <path strokeLinecap="round" strokeLinejoin="round" d="M21 8.25c0-2.485-2.099-4.5-4.688-4.5-1.935 0-3.597 1.126-4.312 2.733-.715-1.607-2.377-2.733-4.313-2.733C5.1 3.75 3 5.765 3 8.25c0 7.22 9 12 9 12s9-4.78 9-12Z" />
                    </svg>
                )

                }
            </div>
        </div >
    )



}

export default Search
