import React, { useState } from 'react';
import AudioPlayer from 'react-h5-audio-player';
import 'react-h5-audio-player/lib/styles.css';

const MP3Player = ({ musics }) => {
    const [currentTrack, setCurrentTrack] = useState(musics[0]);

    const handleNextTrack = () => {
        const currentIndex = musics.indexOf(currentTrack);
        const nextIndex = (currentIndex + 1) % musics.length;
        setCurrentTrack(musics[nextIndex]);
    };

    const handlePreviousTrack = () => {
        const currentIndex = musics.indexOf(currentTrack);
        const prevIndex = (currentIndex - 1 + musics.length) % musics.length;
        setCurrentTrack(musics[prevIndex]);
    };

    return (
        <div className="w-full max-w-lg mx-auto mt-10 bg-[#393442] p-6 rounded-3xl shadow-2xl">
            <div className="flex items-center mb-4">
                <img className="w-16 h-16 rounded-full mr-4" src={currentTrack.img} alt={currentTrack.title} />
                <div>
                    <h3 className="text-white text-lg font-bold">{currentTrack.title}</h3>
                    <p className="text-gray-300">{currentTrack.authorName}</p>
                </div>
            </div>
            <AudioPlayer
                src={currentTrack.src}
                onPlay={() => console.log("Playing")}
                onClickNext={handleNextTrack}
                onClickPrevious={handlePreviousTrack}
                customIcons={{
                    play: (
                        <button className="text-white bg-green-500 rounded-full w-12 h-12 flex items-center justify-center">
                            ▶
                        </button>
                    ),
                    pause: (
                        <button className="text-white bg-red-500 rounded-full w-12 h-12 flex items-center justify-center">
                            ❚❚
                        </button>
                    ),
                    next: (
                        <button className="text-white bg-blue-500 rounded-full w-10 h-10 flex items-center justify-center">
                            ⏭
                        </button>
                    ),
                    previous: (
                        <button className="text-white bg-blue-500 rounded-full w-10 h-10 flex items-center justify-center">
                            ⏮
                        </button>
                    ),
                }}
                customAdditionalControls={[]}
                customVolumeControls={[]}
                showSkipControls={true}
                autoPlayAfterSrcChange={false}
                className="bg-transparent text-white"
            />
        </div>
    );
};

export default MP3Player;
